package _16_obedient_robot;

import org.jointheleague.graphical.robot.Robot;

public class ObedientRobot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Robot shape = new Robot();
	
	shape.penDown();
	for(int i=0;i<4;i++) {
		shape.move(100);
		shape.turn(90);
		
	}
	
	
	
	
	
	
	
	
	
	
	
	
	}

}
